mapper.select:
SELECT CAMB_NUM, CAMB_NAME, CAMB_SUBJECT,
SUBSTRING_INDEX(SUBSTRING_INDEX(CAMB_CONTENT, '</', 1), '>', -1) AS CAMB_CONTENT, CAMB_FILE, 
CAMB_STARTDATE, CAMB_FINDATE, CAMB_DATE, 
IF(floor(datediff(camb_findate, sysdate())/datediff(camb_findate, camb_startdate)*100)>=0,
floor(datediff(camb_findate, sysdate())/datediff(camb_findate, camb_startdate)*100),-1) AS "CAMB_ABLEDATE" 
FROM CAMBOARD 
WHERE (


	(CAMB_NAME LIKE #{search} OR CAMB_SUBJECT LIKE #{search} OR CAMB_CONTENT LIKE #{search})

	CAMB_NAME LIKE #{search} 
	CAMB_SUBJECT LIKE #{search} 
	CAMB_CONTENT LIKE #{search} 

AND 

	(CAMB_STARTDATE>SYSDATE() OR CAMB_STARTDATE<SYSDATE())) 
	(CAMB_STARTDATE<SYSDATE() AND CAMB_FINDATE>SYSDATE())) 
	CAMB_STARTDATE>SYSDATE()) 
	CAMB_FINDATE<SYSDATE()) 

ORDER BY 

	(SELECT CAMB_FINDATE > SYSDATE() AND CAMB_STARTDATE < SYSDATE()) ${order}, CAMB_NUM ${order} 
	${orderBy} ${order} 
	${orderBy} ${order} 
	${orderBy} ${order} 


LIMIT #{contentnum} OFFSET #{pagenum}

SELECT IF(floor(datediff(camb_findate, sysdate())/datediff(camb_findate, camb_startdate)*100)>=0, floor(datediff(camb_findate, sysdate())/datediff(camb_findate, camb_startdate)*100),-1) AS "CAMB_ABLEDATE" FROM CAMBOARD WHERE (
(CAMB_NAME LIKE #{search} OR CAMB_SUBJECT LIKE #{search} OR CAMB_CONTENT LIKE #{search})
CAMB_NAME LIKE #{search} CAMB_SUBJECT LIKE #{search} CAMB_CONTENT LIKE #{search}
AND
(CAMB_STARTDATE>SYSDATE() OR CAMB_STARTDATE<SYSDATE())) (CAMB_STARTDATE<SYSDATE() AND CAMB_FINDATE>SYSDATE())) CAMB_STARTDATE>SYSDATE()) CAMB_FINDATE<SYSDATE())
ORDER BY ${orderBy} ${order} LIMIT #{contentnum} OFFSET #{pagenum}
