mapper.select:
SELECT * 
FROM CAMBOARD 
WHERE (


	(CAMB_NAME LIKE #{search} OR CAMB_SUBJECT LIKE #{search} OR CAMB_CONTENT LIKE #{search})

	CAMB_NAME LIKE #{search} 
	CAMB_SUBJECT LIKE #{search} 
	CAMB_CONTENT LIKE #{search} 

AND 

	(CAMB_DATE>SYSDATE() OR CAMB_DATE<SYSDATE())) 
	CAMB_DATE>SYSDATE()) 
	CAMB_DATE<SYSDATE()) 

ORDER BY ${orderBy} ${order} LIMIT #{contentnum} OFFSET #{pagenum}
